// Ща попробуем. Берите вместе со мной ребятки пиццку кусочек. Лааадно пойдёт!)) 

logger.info("Значение number -- " + me.number.toString());
logger.info("Значение isSigned -- " + me.isSigned.toString());
logger.info("Значение свитча -- " + p.toString());
logger.info("Значение кнопки 1 -- " + b1.toString());
logger.info("Значение кнопки 2 -- " + b2.toString());
logger.info("Значение кнопки 3 -- " + b3.toString());

// Свитч signed <--> unsigned
if (p !== me.RTPV.x) {
    me.number = 0;
    me.isSigned = p === 1;
}
// Кнопка-инкрементатор
else if (b1 > me.RTPV.y) {
    if (
        (me.isSigned && me.number < 7) ||
        (!me.isSigned && me.number < 15)
    ) {
		me.number += 1;   
    }
}
// Кнопка-декрементатор
else if (b2 > me.RTPV.z) {
    if (
        (me.isSigned && me.number > -8) ||
        (!me.isSigned && me.number > 0)
    ) {
		me.number -= 1;   
    }
}
// Кнопка-инвертирователь
else if (b3 > me.RTPV.w) {
	if (me.isSigned) {
        me.number = -me.number - 1;
    }
    else {
        me.number = 15 - me.number;
    }
}

me.RTPV = { 'x': p, 'y': b1, 'z': b2, 'w': b3 };

// Конвертируем в бит эррэй!
result = {
	'L1': ((me.number & 15) & 8) ? 1 : 0,
    'L2': ((me.number & 15) & 4) ? 1 : 0,
    'L3': ((me.number & 15) & 2) ? 1 : 0,
    'L4': ((me.number & 15) & 1) ? 1 : 0,
};
